import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';
import { BASE_URL } from '../constants';

const Airline = () => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    const url = `${BASE_URL}/airline`;

    axios.get(url)
      .then(response => {
        const data = response.data;

        const hours = data.map(item => item.hour || 'Initial');
        const averageSentiment = data.map(item => item.average_sentiment);
        const averageSentimentComputed = data.map(item => item.average_sentiment_computed);
        const absDifference = data.map(item => item.abs_difference);
        const squaredDifference = data.map(item => item.squared_difference);

        setChartData({
          labels: hours,
          datasets: [
            {
              label: 'Average Sentiment',
              data: averageSentiment,
              backgroundColor: 'rgba(255, 99, 132, 0.6)',
            },
            {
              label: 'Average Sentiment Computed',
              data: averageSentimentComputed,
              backgroundColor: 'rgba(54, 162, 235, 0.6)',
            },
            {
              label: 'Absolute Difference',
              data: absDifference,
              backgroundColor: 'rgba(75, 192, 192, 0.6)',
            },
            {
              label: 'Squared Difference',
              data: squaredDifference,
              backgroundColor: 'rgba(153, 102, 255, 0.6)',
            }
          ]
        });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="airline-container">
      <h1>Airline Performance Analysis</h1>
      <h2>Monthly Data Overview</h2>
      <Bar data={chartData} />
    </div>
  );
};

export default Airline;
