import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';
import { BASE_URL } from '../constants';

const Airline = () => {
  const [chartData, setChartData] = useState({ datasets: [] });

  useEffect(() => {
    const url = `${BASE_URL}/airline`;

    axios.get(url)
      .then(response => {
        const data = response.data;

        if (Array.isArray(data) && data.length > 0) {
          let hours = [];
          let averageSentiment = [];
          let averageSentimentComputed = [];

          data.forEach((item, index) => {
            hours.push(item.hour !== undefined ? item.hour : `Unknown ${index}`);
            if (item.average_sentiment !== undefined) {
              averageSentiment.push(item.average_sentiment);
            }
            if (item.average_sentiment_computed !== undefined) {
              averageSentimentComputed.push(item.average_sentiment_computed);
            }
          });

          setChartData({
            labels: hours,
            datasets: [
              {
                label: 'Average Sentiment',
                data: averageSentiment,
                backgroundColor: 'rgba(255, 99, 132, 0.6)',
              },
              {
                label: 'Average Sentiment Computed',
                data: averageSentimentComputed,
                backgroundColor: 'rgba(54, 162, 235, 0.6)',
              }
            ]
          });
        } else {
          console.error('Data is not an array or is empty');
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="airline-container">
      <h1>Airline Performance Analysis</h1>
      <h2>Monthly Data Overview</h2>
      {chartData && <Bar data={chartData} />}
    </div>
  );
};

export default Airline;
